body.script:
        // Sample product data
        const products = [
            {
                id: 1,
                name: "Fresh Tomatoes",
                category: "groceries",
                price: 45,
                image: "https://images.unsplash.com/photo-1549737328-8b9f3252b927?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Fresh organic tomatoes, perfect for salads and cooking"
            },
            {
                id: 2,
                name: "Milk 1L",
                category: "dairy",
                price: 60,
                image: "https://images.unsplash.com/photo-1563636619-e9143da7973b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Fresh cow milk, pasteurized and homogenized"
            },
            {
                id: 3,
                name: "Potato Chips",
                category: "snacks",
                price: 30,
                image: "https://images.unsplash.com/photo-1566478989037-eec170784d0b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Crunchy potato chips with classic salted flavor"
            },
            {
                id: 4,
                name: "Orange Juice",
                category: "beverages",
                price: 120,
                image: "https://images.unsplash.com/photo-1621506289937-a8e4df240d0b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "100% pure orange juice without added sugar"
            },
            {
                id: 5,
                name: "Basmati Rice",
                category: "groceries",
                price: 150,
                image: "https://images.unsplash.com/photo-1586201375761-83865001e31c?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Premium quality basmati rice, 5kg pack"
            },
            {
                id: 6,
                name: "Cheese Slices",
                category: "dairy",
                price: 180,
                image: "https://images.unsplash.com/photo-1552767059-ce182ead6c1b?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Processed cheese slices, 10 slices pack"
            },
            {
                id: 7,
                name: "Biscuits Pack",
                category: "snacks",
                price: 50,
                image: "https://images.unsplash.com/photo-1581235854267-4d6dd6d6f8d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Assorted cream biscuits, 300g pack"
            },
            {
                id: 8,
                name: "Green Tea",
                category: "beverages",
                price: 200,
                image: "https://images.unsplash.com/photo-1594631252845-29fc4cc8cde9?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80",
                description: "Premium green tea with antioxidants, 25 tea bags"
            }
        ];

        // Cart data
        let cart = JSON.parse(localStorage.getItem('balajiMartCart')) || [];

        // DOM Elements
        const productsGrid = document.getElementById('productsGrid');
        const cartIcon = document.getElementById('cartIcon');
        const cartCount = document.getElementById('cartCount');
        const cartSidebar = document.getElementById('cartSidebar');
        const closeCart = document.getElementById('closeCart');
        const cartItems = document.getElementById('cartItems');
        const cartTotal = document.getElementById('cartTotal');
        const checkoutBtn = document.getElementById('checkoutBtn');
        const filterButtons = document.querySelectorAll('.filter-btn');
        const shopNowBtn = document.getElementById('shopNow');

        // Initialize the application
        function initApp() {
            renderProducts();
            updateCartUI();

            // Event Listeners
            cartIcon.addEventListener('click', () => {
                cartSidebar.classList.add('active');
                renderCartItems();
            });

            closeCart.addEventListener('click', () => {
                cartSidebar.classList.remove('active');
            });

            checkoutBtn.addEventListener('click', () => {
                if (cart.length === 0) {
                    alert('Your cart is empty. Add some products first!');
                    return;
                }
                alert(`Thank you for your order! Total: ₹${calculateCartTotal().toFixed(2)}\nYour order will be delivered soon.`);
                cart = [];
                saveCartToStorage();
                updateCartUI();
                renderCartItems();
                cartSidebar.classList.remove('active');
            });

            shopNowBtn.addEventListener('click', () => {
                document.getElementById('products').scrollIntoView({ behavior: 'smooth' });
            });

            // Filter buttons event listeners
            filterButtons.forEach(button => {
                button.addEventListener('click', () => {
                    // Remove active class from all buttons
                    filterButtons.forEach(btn => btn.classList.remove('active'));
                    // Add active class to clicked button
                    button.classList.add('active');

                    const filter = button.getAttribute('data-filter');
                    renderProducts(filter);
                });
            });
        }

        // Render products to the page
        function renderProducts(filter = 'all') {
            productsGrid.innerHTML = '';

            const filteredProducts = filter === 'all' 
                ? products 
                : products.filter(product => product.category === filter);

            filteredProducts.forEach(product => {
                const productCard = document.createElement('div');
                productCard.className = 'product-card';

                productCard.innerHTML = `




${product.name}

${product.description}

₹${product.price.toFixed(2)}

                             Add to Cart


                `;

                productsGrid.appendChild(productCard);
            });

            // Add event listeners to all "Add to Cart" buttons
            document.querySelectorAll('.add-to-cart').forEach(button => {
                button.addEventListener('click', (e) => {
                    const productId = parseInt(e.currentTarget.getAttribute('data-id'));
                    addToCart(productId);
                });
            });
        }

        // Add product to cart
        function addToCart(productId) {
            const product = products.find(p => p.id === productId);

            // Check if product is already in cart
            const existingItem = cart.find(item => item.id === productId);

            if (existingItem) {
                existingItem.quantity += 1;
            } else {
                cart.push({
                    id: product.id,
                    name: product.name,
                    price: product.price,
                    image: product.image,
                    quantity: 1
                });
            }

            saveCartToStorage();
            updateCartUI();

            // Show feedback
            const button = document.querySelector(`.add-to-cart[data-id="${productId}"]`);
            const originalText = button.innerHTML;
            button.innerHTML = ' Added!';
            button.style.backgroundColor = '#27ae60';

            setTimeout(() => {
                button.innerHTML = originalText;
                button.style.backgroundColor = '';
            }, 1000);
        }

        // Render cart items in sidebar
        function renderCartItems() {
            if (cart.length === 0) {
                cartItems.innerHTML = `




Your cart is empty

Add some products to your cart

                `;
                return;
            }

            cartItems.innerHTML = '';

            cart.forEach(item => {
                const cartItem = document.createElement('div');
                cartItem.className = 'cart-item';
                cartItem.innerHTML = `




${item.name}

₹${item.price.toFixed(2)}


                            -
                            ${item.quantity}
                            +
                            Remove


                `;

                cartItems.appendChild(cartItem);
            });

            // Add event listeners for quantity buttons and remove buttons
            document.querySelectorAll('.decrease-quantity').forEach(button => {
                button.addEventListener('click', (e) => {
                    const productId = parseInt(e.currentTarget.getAttribute('data-id'));
                    updateQuantity(productId, -1);
                });
            });

            document.querySelectorAll('.increase-quantity').forEach(button => {
                button.addEventListener('click', (e) => {
                    const productId = parseInt(e.currentTarget.getAttribute('data-id'));
                    updateQuantity(productId, 1);
                });
            });

            document.querySelectorAll('.remove-item').forEach(button => {
                button.addEventListener('click', (e) => {
                    const productId = parseInt(e.currentTarget.getAttribute('data-id'));
                    removeFromCart(productId);
                });
            });

            // Update total
            cartTotal.textContent = `₹${calculateCartTotal().toFixed(2)}`;
        }

        // Update quantity of a cart item
        function updateQuantity(productId, change) {
            const item = cart.find(item => item.id === productId);

            if (item) {
                item.quantity += change;

                if (item.quantity <= 0) {
                    cart = cart.filter(item => item.id !== productId);
                }

                saveCartToStorage();
                updateCartUI();
                renderCartItems();
            }
        }

        // Remove item from cart
        function removeFromCart(productId) {
            cart = cart.filter(item => item.id !== productId);
            saveCartToStorage();
            updateCartUI();
            renderCartItems();
        }

        // Calculate total cart value
        function calculateCartTotal() {
            return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
        }

        // Update cart UI (count and total)
        function updateCartUI() {
            const totalItems = cart.reduce((total, item) => total + item.quantity, 0);
            cartCount.textContent = totalItems;
        }

        // Save cart to localStorage
        function saveCartToStorage() {
            localStorage.setItem('balajiMartCart', JSON.stringify(cart));
        }

        // Initialize the app when DOM is loaded
        document.addEventListener('DOMContentLoaded', initApp);
